import React, { useState } from 'react';
import {
  Paper,
  Typography,
  List,
  ListItem,
  Card,
  CardContent,
  Box,
  Button,
  Chip,
  Divider,
  Collapse,
  IconButton,
} from '@mui/material';
import { 
  Send as SendIcon, 
  Schedule as ScheduleIcon,
  ExpandMore as ExpandMoreIcon,
  ExpandLess as ExpandLessIcon 
} from '@mui/icons-material';
import XIcon from '../Icons/XIcon';

const ReplyQueue = ({ queuedReplies = [] }) => {
  const [sending, setSending] = useState(new Set());

  const handleSendReply = async (replyId) => {
    setSending(prev => new Set([...prev, replyId]));
    // TODO: Implement actual sending
    await new Promise(resolve => setTimeout(resolve, 1000));
    setSending(prev => {
      const next = new Set(prev);
      next.delete(replyId);
      return next;
    });
  };

  return (
    <Paper sx={{ 
      p: 2, 
      borderRadius: 2, 
      bgcolor: 'background.paper', 
      height: '85vh',
    }}>
      <Box sx={{ 
        display: 'flex', 
        alignItems: 'center', 
        gap: 1,
        mb: 2,
      }}>
        <ScheduleIcon fontSize="small" />
        <Typography variant="h6">
          Reply Queue ({queuedReplies.length})
        </Typography>
      </Box>

      {queuedReplies.length === 0 ? (
        <Box sx={{ textAlign: 'center', py: 3 }}>
          <Typography color="text.secondary">No replies queued</Typography>
        </Box>
      ) : (
        <List sx={{ maxHeight: '75vh', overflow: 'auto' }}>
          {queuedReplies.map((reply) => (
            <ListItem key={reply.id} sx={{ px: 0 }}>
              <Card variant="outlined" sx={{ width: '100%' }}>
                <CardContent>
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 1 }}>
                    <XIcon fontSize="small" />
                    <Typography variant="subtitle2">
                      {reply.username}
                    </Typography>
                  </Box>

                  <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
                    {reply.originalTweet}
                  </Typography>

                  <Typography variant="body2" sx={{ mb: 2 }}>
                    {reply.replyText}
                  </Typography>

                  <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                    <Chip 
                      size="small" 
                      label={`Queued: ${new Date(reply.queuedAt).toLocaleString()}`}
                    />
                    <Button
                      size="small"
                      variant="contained"
                      startIcon={<SendIcon />}
                      onClick={() => handleSendReply(reply.id)}
                      disabled={sending.has(reply.id)}
                    >
                      {sending.has(reply.id) ? 'Sending...' : 'Send Now'}
                    </Button>
                  </Box>
                </CardContent>
              </Card>
            </ListItem>
          ))}
        </List>
      )}
    </Paper>
  );
};

export default ReplyQueue;
