import React, { useEffect, useState } from 'react';
import {
  Box,
  Container,
  Grid,
  Typography,
  AppBar,
  Toolbar,
  Chip,
} from '@mui/material';
import AccountTracker from './AccountTracker';
import TrackedAccountsList from './TrackedAccountsList';
import ReplyQueue from './ReplyQueue';

const Dashboard = () => {
  const [dbStatus, setDbStatus] = useState('Checking...');
  const [queuedReplies, setQueuedReplies] = useState([]);

  useEffect(() => {
    fetch('/api/test-db')
      .then(res => res.json())
      .then(data => setDbStatus(data.status))
      .catch(err => setDbStatus('Database connection failed'));
  }, []);

  const handleAddToQueue = (reply) => {
    setQueuedReplies(prev => [...prev, {
      id: Date.now(),
      queuedAt: new Date(),
      ...reply
    }]);
  };

  return (
    <Box sx={{ flexGrow: 1 }}>
      <AppBar position="static" color="primary" elevation={0}>
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            Express AI Dashboard
          </Typography>
          <Chip 
            label={`Status: ${dbStatus}`}
            color={dbStatus.includes('failed') ? 'error' : 'success'}
            sx={{ ml: 2 }}
          />
        </Toolbar>
      </AppBar>

      <Container maxWidth="xl" sx={{ mt: 4, mb: 4 }}>
        <Grid container spacing={3}>
          {/* Top Row - Account Tracker */}
          <Grid item xs={12}>
            <AccountTracker />
          </Grid>

          {/* Main Content */}
          <Grid container item spacing={3}>
            {/* Tracked Accounts List - Dynamic width */}
            <Grid item xs={12} md={true}>  {/* 'true' makes it take remaining space */}
              <TrackedAccountsList onAddToQueue={handleAddToQueue} />
            </Grid>

            {/* Reply Queue - Fixed width when open */}
            <Grid item xs={12} md="auto" sx={{ 
              transition: 'all 0.3s ease-in-out',
              minWidth: { md: '300px' }
            }}>
              <ReplyQueue queuedReplies={queuedReplies} />
            </Grid>
          </Grid>
        </Grid>
      </Container>
    </Box>
  );
};

export default Dashboard;
