import React, { useEffect, useState } from 'react';
import {
  Box,
  Container,
  Grid,
  Typography,
  AppBar,
  Toolbar,
  Chip,
} from '@mui/material';
import ReplyTester from './ReplyTester';
import TwitterTester from './TwitterTester';
import AccountTracker from './AccountTracker';
import TrackedAccountsList from './TrackedAccountsList';
import './Dashboard.css';

const Dashboard = () => {
  const [dbStatus, setDbStatus] = useState('Checking...');

  useEffect(() => {
    fetch('/api/test-db')
      .then(res => res.json())
      .then(data => setDbStatus(data.status))
      .catch(err => setDbStatus('Database connection failed'));
  }, []);

  return (
    <Box sx={{ flexGrow: 1 }}>
      <AppBar position="static" color="primary" elevation={0}>
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            Express AI Dashboard
          </Typography>
          <Chip 
            label={`Status: ${dbStatus}`}
            color={dbStatus.includes('failed') ? 'error' : 'success'}
            sx={{ ml: 2 }}
          />
        </Toolbar>
      </AppBar>

      <Container maxWidth="xl" sx={{ mt: 4, mb: 4 }}>
        <Grid container spacing={3}>
          {/* Left Column */}
          <Grid item xs={12} md={4}>
            <Grid container spacing={3}>
              <Grid item xs={12}>
                <AccountTracker />
              </Grid>
              <Grid item xs={12}>
                <ReplyTester />
              </Grid>
            </Grid>
          </Grid>

          {/* Middle Column */}
          <Grid item xs={12} md={4}>
            <TrackedAccountsList />
          </Grid>

          {/* Right Column */}
          <Grid item xs={12} md={4}>
            <Grid container spacing={3}>
              <Grid item xs={12}>
                <TwitterTester />
              </Grid>
              {/* Add more components here */}
            </Grid>
          </Grid>
        </Grid>
      </Container>
    </Box>
  );
};

export default Dashboard;
