import React, { useState } from 'react';
import {
  Box,
  Paper,
  Typography,
  TextField,
  Button,
  Grid,
  Alert,
  CircularProgress,
  InputAdornment,
} from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import XIcon from '../Icons/XIcon';

const AccountTracker = () => {
  const [username, setUsername] = useState('');
  const [trackingResult, setTrackingResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const startTracking = async () => {
    try {
      setLoading(true);
      const response = await fetch('/api/twitter/track', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username: username.replace('@', '') }),
      });

      const data = await response.json();
      if (!data.success) {
        throw new Error(data.error || 'Failed to track account');
      }

      setTrackingResult(data.data);
      setUsername('');
    } catch (error) {
      console.error('Error:', error);
      setTrackingResult({ 
        error: error.message.includes('rate limit') 
          ? 'Twitter API rate limit reached. Please try again in a few minutes.'
          : error.message 
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <Paper 
      sx={{ 
        p: 2,
        borderRadius: 2, 
        bgcolor: 'background.paper',
        minHeight: '80px',
      }}
    >
      <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
        <Box sx={{ flex: 1, maxWidth: 600 }}>
          <Typography variant="subtitle1" gutterBottom sx={{ 
            display: 'flex', 
            alignItems: 'center', 
            gap: 1,
            mb: 1
          }}>
            <XIcon color="primary" fontSize="small" />
            Track New Account
          </Typography>

          <TextField
            fullWidth
            size="small"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Enter username to track"
            variant="outlined"
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">@</InputAdornment>
              ),
              endAdornment: (
                <InputAdornment position="end">
                  <Button
                    variant="contained"
                    size="small"
                    onClick={startTracking}
                    disabled={loading || !username}
                    sx={{
                      minWidth: 'auto',
                      background: 'linear-gradient(45deg, #000 30%, #333 90%)',
                      boxShadow: '0 3px 5px 2px rgba(0, 0, 0, .3)',
                      '&:hover': {
                        background: 'linear-gradient(45deg, #111 30%, #444 90%)',
                      }
                    }}
                  >
                    {loading ? (
                      <CircularProgress size={16} color="inherit" />
                    ) : (
                      <AddIcon fontSize="small" />
                    )}
                  </Button>
                </InputAdornment>
              ),
              sx: {
                pr: 0.5,
              }
            }}
          />
        </Box>
      </Box>

      {trackingResult && (
        <Box sx={{ mt: 2 }}>
          {trackingResult.error ? (
            <Alert severity="error" sx={{ mb: 1 }}>
              {trackingResult.error}
            </Alert>
          ) : (
            <Alert severity="success" sx={{ mb: 1 }}>
              Now tracking: @{trackingResult.user?.username}
            </Alert>
          )}
        </Box>
      )}
    </Paper>
  );
};

export default AccountTracker;
