import { createTheme } from '@mui/material/styles';

export const theme = createTheme({
  palette: {
    primary: {
      main: '#64B5F6', // Light blue
      light: '#90CAF9',
      dark: '#42A5F5',
      contrastText: '#fff',
    },
    secondary: {
      main: '#81C784', // Light green
      light: '#A5D6A7',
      dark: '#66BB6A',
      contrastText: '#fff',
    },
    background: {
      default: '#F5F7FA',
      paper: '#FFFFFF',
    },
    text: {
      primary: '#2C3E50',
      secondary: '#34495E',
    },
  },
  components: {
    MuiCard: {
      styleOverrides: {
        root: {
          borderRadius: 12,
          boxShadow: '0 2px 16px rgba(0,0,0,0.08)',
        },
      },
    },
  },
});
