import React, { useState, useEffect } from 'react';
import {
  Box,
  Paper,
  Typography,
  List,
  ListItem,
  ListItemButton,
  ListItemText,
  ListItemAvatar,
  Avatar,
  Collapse,
  Card,
  CardContent,
  CardHeader,
  Divider,
  Chip,
  IconButton,
  TextField,
  Button,
  Grid,
} from '@mui/material';
import { 
  ExpandMore, 
  ExpandLess, 
  Edit as EditIcon,
  Refresh as RefreshIcon,
  Psychology as AIIcon,
  Send as SendIcon,
  Check as CheckIcon
} from '@mui/icons-material';
import XIcon from '../Icons/XIcon';

const TrackedAccountsList = ({ onAddToQueue }) => {
  const [accounts, setAccounts] = useState([]);
  const [selectedAccount, setSelectedAccount] = useState(null);
  const [loading, setLoading] = useState(true);
  const [suggestions, setSuggestions] = useState({});
  const [editingReply, setEditingReply] = useState(null);
  const [editingContext, setEditingContext] = useState(false);
  const [context, setContext] = useState('');
  const [approvedReplies, setApprovedReplies] = useState(new Set());

  useEffect(() => {
    fetchTrackedAccounts();
  }, []);

  const fetchTrackedAccounts = async () => {
    try {
      const response = await fetch('/api/twitter/tracked-accounts');
      const data = await response.json();
      if (data.success) {
        setAccounts(data.data);
      }
    } catch (error) {
      console.error('Error fetching tracked accounts:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleAccountClick = async (account) => {
    setSelectedAccount(selectedAccount?.username === account.username ? null : account);

    // Generate replies for all tweets that don't have suggestions yet
    if (account.tweets) {
      account.tweets.forEach(async (tweet) => {
        if (!suggestions[tweet.id]) {
          await generateReply(tweet);
        }
      });
    }
  };

  const generateReply = async (tweet) => {
    try {
      // Show loading state while generating
      setSuggestions(prev => ({
        ...prev,
        [tweet.id]: 'Generating suggestion...'
      }));

      const response = await fetch('/api/test-reply', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ 
          tweet: tweet.text,
          context: context || 'Be professional and friendly'
        }),
      });

      const data = await response.json();
      setSuggestions(prev => ({
        ...prev,
        [tweet.id]: data.reply
      }));
    } catch (error) {
      console.error('Error generating reply:', error);
      setSuggestions(prev => ({
        ...prev,
        [tweet.id]: 'Failed to generate reply'
      }));
    }
  };

  const handleApproveReply = (tweet, reply) => {
    setApprovedReplies(prev => new Set([...prev, tweet.id]));
    onAddToQueue({
      username: selectedAccount.username,
      originalTweet: tweet.text,
      replyText: reply,
      tweetId: tweet.id
    });
  };

  return (
    <Paper 
      sx={{ 
        p: 3, 
        borderRadius: 2, 
        bgcolor: 'background.paper',
        height: '85vh',  // Set fixed height
        overflow: 'auto' // Enable scrolling
      }}
    >
      <Typography variant="h5" gutterBottom sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
        <XIcon color="primary" />
        Tracked Accounts
      </Typography>

      {loading ? (
        <Typography color="text.secondary">Loading accounts...</Typography>
      ) : accounts.length === 0 ? (
        <Typography color="text.secondary">No accounts tracked yet</Typography>
      ) : (
        <List>
          {accounts.map((account) => (
            <React.Fragment key={account.username}>
              <ListItem disablePadding>
                <ListItemButton onClick={() => handleAccountClick(account)}>
                  <ListItemAvatar>
                    <Avatar sx={{ bgcolor: 'primary.main' }}>
                      <XIcon />
                    </Avatar>
                  </ListItemAvatar>
                  <ListItemText 
                    primary={account.username}
                    secondary={`Last checked: ${new Date(account.lastChecked).toLocaleString()}`}
                  />
                  {account.keywords.length > 0 && (
                    <Box sx={{ display: 'flex', gap: 1, mr: 2 }}>
                      {account.keywords.map(keyword => (
                        <Chip 
                          key={keyword} 
                          label={keyword} 
                          size="small" 
                          color="primary" 
                          variant="outlined"
                        />
                      ))}
                    </Box>
                  )}
                  {selectedAccount?.username === account.username ? <ExpandLess /> : <ExpandMore />}
                </ListItemButton>
              </ListItem>

              <Collapse in={selectedAccount?.username === account.username} timeout="auto" unmountOnExit>
                <Box sx={{ pl: 4, pr: 2, pb: 2 }}>
                  {account.tweets?.map((tweet) => (
                    <Grid container spacing={2} key={tweet.id} sx={{ mb: 3 }}>
                      {/* Original Tweet - smaller */}
                      <Grid item xs={12} md={5}>
                        <Paper 
                          elevation={0} 
                          sx={{ 
                            height: '100%',
                            border: '1px solid',
                            borderColor: 'divider',
                            borderRadius: 2,
                            position: 'relative',
                            overflow: 'hidden',
                            display: 'flex',
                            flexDirection: 'column',
                          }}
                        >
                          {/* Title Bar */}
                          <Box sx={{
                            p: 1.5,
                            bgcolor: 'grey.50',
                            borderBottom: '1px solid',
                            borderColor: 'divider',
                            display: 'flex',
                            alignItems: 'center',
                            gap: 1
                          }}>
                            <XIcon fontSize="small" color="action" />
                            <Typography variant="subtitle2" color="text.secondary">
                              Original Tweet
                            </Typography>
                          </Box>

                          {/* Content */}
                          <Box sx={{ 
                            p: 2,
                            flex: 1,
                          }}>
                            {/* Header with username */}
                            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 2 }}>
                              <XIcon fontSize="small" />
                              <Typography variant="subtitle2">
                                {tweet.username}
                              </Typography>
                            </Box>

                            {/* Tweet content */}
                            <Typography 
                              variant="body2" 
                              sx={{ 
                                flex: 1,
                                mb: 2,
                              }}
                            >
                              {tweet.text}
                            </Typography>
                          </Box>

                          {/* Bottom Bar */}
                          <Box sx={{ 
                            mt: 'auto',
                            p: 2,
                            borderTop: '1px solid',
                            borderColor: 'divider',
                            bgcolor: 'grey.50',
                          }}>
                            <Box sx={{ 
                              display: 'flex', 
                              justifyContent: 'center',
                              gap: 3,
                            }}>
                              <Typography variant="caption" color="text.secondary">
                                â™¥ {tweet.public_metrics?.like_count || 0}
                              </Typography>
                              <Typography variant="caption" color="text.secondary">
                                â†º {tweet.public_metrics?.retweet_count || 0}
                              </Typography>
                              <Typography variant="caption" color="text.secondary">
                                ðŸ’¬ {tweet.public_metrics?.reply_count || 0}
                              </Typography>
                            </Box>
                          </Box>
                        </Paper>
                      </Grid>

                      {/* Suggested Reply - larger */}
                      <Grid item xs={12} md={7}>
                        <Paper 
                          elevation={0} 
                          sx={{ 
                            height: '100%',
                            border: '1px solid',
                            borderColor: 'divider',
                            borderRadius: 2,
                            position: 'relative',
                            overflow: 'hidden',
                            display: 'flex',
                            flexDirection: 'column',
                          }}
                        >
                          {/* Title Bar */}
                          <Box sx={{
                            p: 1.5,
                            bgcolor: 'grey.50',
                            borderBottom: '1px solid',
                            borderColor: 'divider',
                            display: 'flex',
                            alignItems: 'center',
                            gap: 1
                          }}>
                            <XIcon fontSize="small" color="action" />
                            <Typography variant="subtitle2" color="text.secondary">
                              Suggested Reply
                            </Typography>
                          </Box>

                          {/* Content */}
                          <Box sx={{ 
                            p: 2,
                            flex: 1,
                          }}>
                            {editingReply === tweet.id ? (
                              <TextField
                                fullWidth
                                multiline
                                rows={4}
                                defaultValue={suggestions[tweet.id]}
                                placeholder="Edit reply..."
                              />
                            ) : (
                              <Typography variant="body2" sx={{ minHeight: '80px' }}>
                                {suggestions[tweet.id] || 'Generating suggestion...'}
                              </Typography>
                            )}
                          </Box>

                          {/* Bottom Bar */}
                          <Box sx={{ 
                            mt: 'auto',
                            p: 2,
                            borderTop: '1px solid',
                            borderColor: 'divider',
                            bgcolor: 'grey.50',
                          }}>
                            <Box sx={{ 
                              display: 'flex', 
                              justifyContent: 'space-between', 
                              alignItems: 'center',
                            }}>
                              {/* Left - AI Edit */}
                              <Button
                                size="small"
                                startIcon={<AIIcon />}
                                onClick={() => {
                                  setEditingContext(true);
                                  setContext('');
                                }}
                              >
                                Edit with AI
                              </Button>

                              {/* Center - Approve */}
                              {!approvedReplies.has(tweet.id) ? (
                                <Button
                                  variant="contained"
                                  size="small"
                                  startIcon={<CheckIcon />}
                                  onClick={() => handleApproveReply(tweet, suggestions[tweet.id])}
                                  disabled={!suggestions[tweet.id]}
                                >
                                  Approve
                                </Button>
                              ) : (
                                <Chip
                                  label="Approved"
                                  color="success"
                                  size="small"
                                />
                              )}

                              {/* Right - Text Edit */}
                              <Button
                                size="small"
                                startIcon={<EditIcon />}
                                onClick={() => setEditingReply(editingReply === tweet.id ? null : tweet.id)}
                              >
                                Edit Text
                              </Button>
                            </Box>
                          </Box>
                        </Paper>
                      </Grid>

                      {/* AI Context Editor */}
                      <Grid item xs={12}>
                        <Collapse in={editingContext} timeout="auto">
                          <Card variant="outlined">
                            <CardContent>
                              <Typography variant="subtitle2" gutterBottom>
                                Update AI Context
                              </Typography>
                              <TextField
                                fullWidth
                                size="small"
                                multiline
                                rows={2}
                                value={context}
                                onChange={(e) => setContext(e.target.value)}
                                placeholder="Enter new instructions for AI (e.g., 'Be more professional', 'Use casual tone')"
                              />
                              <Box sx={{ mt: 2, display: 'flex', gap: 1, justifyContent: 'flex-end' }}>
                                <Button 
                                  size="small" 
                                  onClick={() => setEditingContext(false)}
                                >
                                  Cancel
                                </Button>
                                <Button 
                                  size="small" 
                                  variant="contained"
                                  onClick={() => {
                                    generateReply(tweet);
                                    setEditingContext(false);
                                  }}
                                >
                                  Regenerate
                                </Button>
                              </Box>
                            </CardContent>
                          </Card>
                        </Collapse>
                      </Grid>
                    </Grid>
                  ))}
                </Box>
              </Collapse>
              <Divider />
            </React.Fragment>
          ))}
        </List>
      )}
    </Paper>
  );
};

export default TrackedAccountsList;
